var graphData;

function imGraph(target, data) {
    var chartoptions = {
        global: { 
            useUTC: false
        },
        chart: {
            renderTo: 'graph',
            /*margin: [25, 40, 25, 40],*/
            marginTop: 0,
            marginBottom: 40,
            borderRadius: 0,
            backgroundColor: '#ffffff'
        },
        title: {
            text: null
        },
        colors: ['#0088cc', '#339900'],
        credits: {
            enabled: false
        },
        legend: {
            enabled: false
        },
        plotOptions: {
            area: {
                lineWidth: 2.5,
                fillOpacity: .1,
                marker: {
                    lineColor: '#fff',
                    lineWidth: 1,
                    radius: 3.5,
                    symbol: 'circle'
                },
                shadow: false
            },
            column: {
                lineWidth: 16,
                shadow: false,
                borderWidth: 0,
                groupPadding: .05
            }
        },
        xAxis: {
            type: 'datetime',
            title: {
                text: null
            },
            tickmarkPlacement: 'off',
            dateTimeLabelFormats: {
                day: '%b %e'
            },
            gridLineColor: '#eeeeee',
            gridLineWidth: 0,
            labels: {
                style: {
                    color: '#999999'
                }
            }
        },
        yAxis: [
            {
                showFirstLabel: false,
                showLastLabel: false,
                tickPixelInterval: 50,
                endOnTick: false,
                title: {
                    text: null
                },
                gridLineColor: '#eeeeee',
                gridLineWidth: .5,
                zIndex: 2,
                labels: {
                    align: 'right',
                    style: {
                        color: '#999999'
                    },
                    x: -4
                }
            }, {
                showFirstLabel: false,
                showLastLabel: false,
                tickPixelInterval: 50,
                endOnTick: false,
                title: {
                    text: null
                },
                opposite: true,
                gridLineColor: '#eeeeee',
                gridLineWidth: .5,
                zIndex: 1,
                labels: {
                    align: 'left',
                    style: {
                        color: '#999999'
                    },
                    x: 4
                }
            }
        ],
        tooltip: {
            shadow: false,
            borderRadius: 3,
            shared: true,
            formatter: function(args) {
                var y1 = (parseInt(this.points[0].y) == this.points[0].y) ? this.points[0].y : this.points[0].y.toFixed(2);
                var y2 = (parseInt(this.points[1].y) == this.points[1].y) ? this.points[1].y : this.points[1].y.toFixed(2);
                var line1 = '<span style="font-size: 10px">' + moment(this.x).format('dddd, MMM D, YYYY') + '</span>';
                var line2 = '<span style="color: #08c">' + args.chart.series[0].name + ':</span> <b>' + y1 + '</b>';
                var line3 = '<span style="color: #390">' + args.chart.series[1].name + ':</span> <b>' + y2 + '</b>';
                return line1 + ' <br /> ' + line2 + ' <br /> ' + line3;
            }
        },
        series: [
            {
                type: 'column'              
            },
            {
                type: 'area',
                yAxis: 1                    
            } 
        ]
    };

    if ($('select[name="graph_metric1"]').length > 0) {
        var metric1_key = $('select[name="graph_metric1"]').val();
        var metric1_text = $('select[name="graph_metric1"]').select2('data')[0].text;

        var metric2_key = $('select[name="graph_metric2"]').val();
        var metric2_text = $('select[name="graph_metric2"]').select2('data')[0].text;
    } else {
        metric1_key = 'people';
        metric1_text = 'People';
        metric2_key = 'conversions';
        metric2_text = 'Conversions';
    }

    var series1 = [], series2 = [], totals = [];

    for (var row in data) {

        data[row]['convrate'] = data[row]['people'] == 0 ? 0 : ((data[row]['conversions'] / data[row]['people']) * 100).toFixed(2);
        data[row]['cpa'] = data[row]['conversions'] == 0 ? 0 : data[row]['cost'] / data[row]['conversions'];
        data[row]['cpc'] = data[row]['clicks'] == 0 ? 0 : data[row]['cost'] / data[row]['clicks'];
        data[row]['rpp'] = data[row]['people'] == 0 ? 0 : data[row]['revenue'] / data[row]['people'];
        data[row]['profit'] = data[row]['revenue'] - data[row]['cost'];

        if (data[row]['date'] == null || data[row]['date'].length == 0) {
            totals = data[row];
            continue;
        }

        var val = parseFloat(data[row][metric1_key]);
        series1.push([moment(data[row]['date']).valueOf(), val]);

        var val = parseFloat(data[row][metric2_key]);
        series2.push([moment(data[row]['date']).valueOf(), val]);

    }

    if (totals.length == 0 && data[0]) {
        totals = data[0];
    }

    chartoptions.series[0].name = metric1_text;
    chartoptions.series[0].data = series1;
    chartoptions.series[0].pointStart = series1[0][0].valueOf();

    chartoptions.series[1].name = metric2_text
    chartoptions.series[1].data = series2;
    chartoptions.series[1].pointStart = series2[0][0].valueOf();

    if ($('#' + target + ' .placeholder').length > 0) {
        chartoptions.chart.renderTo = $('#' + target + ' .placeholder')[0];
    } else {
        chartoptions.chart.renderTo = $('#' + target)[0];
    }

    var h1, h2;

    if (metric1_key == 'cost' || metric1_key == 'cpa' || metric1_key == 'cpc' || metric1_key == 'revenue' || metric1_key == 'rpp' || metric1_key == 'profit') {
        h1 = format_money(totals[metric1_key]);
    } else if (metric1_key == 'convrate') {
        h1 = format_rate(totals[metric1_key] / 100);
    } else {
        h1 = add_commas(totals[metric1_key]);
    }

    if (metric2_key == 'cost' || metric2_key == 'cpa' || metric2_key == 'cpc' || metric2_key == 'revenue' || metric2_key == 'rpp' || metric2_key == 'profit') {
        h2 = format_money(totals[metric2_key]);
    } else if (metric2_key == 'convrate') {
        h2 = format_rate(totals[metric2_key] / 100);
    } else {
        h2 = add_commas(totals[metric2_key]);
    }

    $('.metric1 h1').html(h1);
    $('.metric1 h2').html(metric1_text);

    $('.metric2 h1').html(h2);
    $('.metric2 h2').html(metric2_text);

    graph = new Highcharts.Chart(chartoptions);
    graphData = data;
}


function metricGraph(target, data, label) {

    var chartoptions = {
        global: { 
            useUTC: false
        },
        chart: {
            renderTo: 'graph',
            margin: [-2, -2, -2, -2],
            borderRadius: 0,
            backgroundColor: '#ffffff'
        },
        title: {
            text: null
        },
        colors: ['#0088cc', '#339900'],
        credits: {
            enabled: false
        },
        legend: {
            enabled: false
        },
        plotOptions: {
            column: {
                lineWidth: 16,
                shadow: false,
                borderWidth: 0,
                groupPadding: .05
            },
            area: {
                lineColor: '#0088cc',
                lineWidth: 4,
                marker: { enabled: false }
            }
        },
        xAxis: {
            type: 'datetime',
            title: {
                text: null
            },
            tickmarkPlacement: 'off',
            gridLineWidth: 0,
            labels: {
                enabled: false
            }
        },
        yAxis: [
            {
                gridLineWidth: 0,
                minorGridLineWidth: 0,
                showFirstLabel: false,
                showLastLabel: false,
                title: {
                    text: null
                },
                labels: {
                    enabled: false
                },
                min: 0
            }
        ],
        tooltip: {
            enabled: false
        },
        series: [
            {
                type: 'area',
                states: {
                    hover: {
                        enabled: false
                    }
                }           
            }
        ]
    };

    var series1 = [];

    for (var row in data) {
        data[row]['convrate'] = data[row]['people'] == 0 ? 0 : ((data[row]['conversions'] / data[row]['people']) * 100).toFixed(2);
        data[row]['goalvalue'] = data[row]['conversions'] == 0 ? 0 : data[row]['revenue'] / data[row]['conversions'];
        data[row]['cpa'] = data[row]['conversions'] == 0 ? 0 : data[row]['cost'] / data[row]['conversions'];
        data[row]['cpc'] = data[row]['clicks'] == 0 ? 0 : data[row]['cost'] / data[row]['clicks'];
        data[row]['rpp'] = data[row]['people'] == 0 ? 0 : data[row]['revenue'] / data[row]['people'];
        data[row]['profit'] = data[row]['revenue'] - data[row]['cost'];
        data[row]['roi'] = data[row]['cost'] == 0 ? 0 : (((data[row]['revenue'] - data[row]['cost']) / data[row]['cost']) * 100).toFixed(2);

        var val = parseFloat(data[row][label]);
        series1.push(val);
    }

    chartoptions.series[0].data = series1;
    //chartoptions.series[0].pointStart = series1[0][0].valueOf();

    chartoptions.chart.renderTo = target;

    graph = new Highcharts.Chart(chartoptions);
};


