    var message = "We've detected that you have an ad blocker enabled, which may block some of your reports from loading. Please disable your ad blocker on this domain.";
    var tryMessage = function() {
        setTimeout(function() {
            if(!document.getElementsByClassName) return;
            var ads = document.getElementsByClassName('afs_ads'),
                ad  = ads[ads.length - 1];

            if(!ad || ad.innerHTML.length == 0 || ad.clientHeight === 0) {
                alert(message);
            }
        }, 2000);
    }
    if(window.addEventListener) {
        window.addEventListener('load', tryMessage, false);
    } else {
        window.attachEvent('onload', tryMessage); //IE
    }

    /*
     *  Feedback Form
     */
    var fb = '';
    $('#feedback textarea').val(fb);

    $('#feedback textarea').blur(function() {
        if ($(this).val() == '')
            $(this).val(fb);
    });

    $('#feedback textarea').focus(function() {
        if ($(this).val() == fb) {
            $(this).val('');
        }
    });

    $('#feedback form').submit(function() {
        if ($(this).find('textarea').val() == fb) {
            return false;
        }

        var message = $(this).find('textarea').val();
        var url = window.location.href;

        $(this).find('input[type="submit"]').attr('disabled', 'disabled').val('Sending...');

        var data = {
            message: message,
            url: url
        }

        $.ajax({
            type: 'POST',
            url: '/api/feedback',
            data: data,
            dataType: 'json',
            success: function() {
                $('#feedback').html('Thank you! Your feedback has been sent.');
            }
        });

        return false;
    });

