function add_commas(val) {
    if (!isFinite(val) || val == 0 || val == null) return 0;
    val = val + '';
    var whole = val;
    var decimal = null;

    if (val.indexOf('.') != -1) {
        var parts = val.split('.');
        whole = parts[0];
        decimal = parts[1];
    }

    var result = '';
    var pos = 0;
    for (var i = whole.length - 1; i >= 0; i--) {
        if (pos > 0 && pos % 3 == 0)
            result = ',' + result;
        result = whole.charAt(i) + result;
        pos++;
    }

    if (decimal != null)
        result = result + '.' + decimal;

    return result;
}

function format_money(val, round_thousands) {
    if (!isFinite(val) || val == 0 || val == null) {
        return currency_symbol + '0.00';
    }
    var decimals = 2;
    if (typeof round_thousands != 'undefined' && val > 10000) {
        decimals = 0;
    }
    if (parseFloat(val) < 0) {
        return '<span class="negative">(' + currency_symbol + (parseFloat(val) * -1).toFixed(decimals) + ')</span>';
    }
    return currency_symbol + add_commas(parseFloat(val).toFixed(decimals));
}

function format_rate(val) {
    if (isNaN(val) || val == 0 || val == null) return '0%';
    if (!isFinite(val)) return '100%';
    val = Math.round(val * 1000) / 10;
    return val + '%';
}

function format_hour(val) {
    if (val == 0)
        return '12:00 - 12:59 AM';
    else if (val < 12)
        return val + ':00 - ' + val + ':59 AM';
    else if (val == 12)
        return '12:00 - 12:59 PM';
    else
        return (val - 12) + ':00 - ' + (val - 12) + ':59 PM';
}

function trim_str(val, length) {
    if (val == null) return val;
    if (val.length <= length) return val;
    if (val.substr(0, 7) == 'http://' || val.substr(0,8) == 'https://') {
        length = length - 3;
        var fh = Math.floor(length/2);
        var sh = Math.ceil(length/2);
        return val.substr(0, fh) + '...' + val.substr(val.length - sh);
    } else {
        return val.substr(0, length - 3) + '...';
    }
}

function ucfirst(val) {
    if (val == null || val.length == 0) return val;
    return val.charAt(0).toUpperCase() + val.substr(1);
}

function set_cookie(c_name, value, expiredays) {
    var name = c_name;
    var exdate = new Date();
    exdate.setDate(exdate.getDate() + expiredays);
    var cookie = name + "=" + escape(value);
    if (expiredays != null)
        cookie += "; expires=" + exdate.toUTCString();
    cookie += "; path=/";
    document.cookie = cookie;
}

function get_cookie(c_name) {
    var name = c_name;
    if (document.cookie.length > 0) {
        c_start = document.cookie.indexOf(name + "=");
        if (c_start != -1) {
            c_start = c_start + name.length + 1;
            c_end = document.cookie.indexOf(";", c_start);
            if (c_end == -1) c_end = document.cookie.length;
            return unescape(document.cookie.substring(c_start, c_end));
        }
    }
    return null;
}

